mapper.select:
select year(dt_add) year,month(dt_add) month,count(*) total from kj_icbc where year(SYSDATE())-year(dt_add) < 6 and app=0 and gems_fs_id in(select id from assess_fs af where up_id=${up_id} or id =${id})
and gems_fs_id=${gems_id}
and id in(select icbc_id from kjs_icbc_kk where year(SYSDATE())-year(dt_add) < 6 and kk_loan_stateid=${city_id})
and year(dt_add)=${timedate}
GROUP BY year(dt_add),month(dt_add) ORDER BY dt_add DESC limit 9

select sum(case when iekir.fk_money >= 30000 and iekir.fk_money < 100000 then 1 end) singular1, sum(case when iekir.fk_money >= 100000 and iekir.fk_money < 200000 then 1 end) singular2, sum(case when iekir.fk_money >= 200000 and iekir.fk_money < 500000 then 1 end) singular3, sum(case when iekir.fk_money >= 500000 then 1 end) singular4 from icbc_erp_kj_icbc_result iekir,kj_icbc ki where month(iekir.fk_date)=MONTH(SYSDATE()) and YEAR(iekir.fk_date)=year(SYSDATE()) and iekir.status = 62 and ki.app=0 and iekir.result_1_code=1 and iekir.icbc_id=ki.id
and ki.gems_fs_id=${gems_id}
and ki.id in(select icbc_id from kjs_icbc_kk where year(SYSDATE())-year(dt_add) < 6 and kk_loan_stateid=${city_id})
and year(ki.dt_add)=${timedate}
and ki.gems_fs_id in(select id from assess_fs af where up_id=${up_id} or id =${id})

select count(iekir.result_1_code) zxok,iekir.result_1_code zxok_tag from icbc_erp_kj_icbc_result iekir,kj_icbc ki where iekir.`status`=3 and ki.app=0 and ki.id=iekir.icbc_id
and ki.gems_fs_id=${gems_id}
and ki.id in(select icbc_id from kjs_icbc_kk where year(SYSDATE())-year(dt_add) < 6 and kk_loan_stateid=${city_id})
and year(ki.dt_add)=${timedate}
and ki.gems_fs_id in(select id from assess_fs af where up_id=${up_id} or id =${id}) GROUP BY iekir.result_1_code ORDER BY iekir.result_1_code LIMIT 0,2

select year(iekir.dt_sub) year, month(iekir.dt_sub) month, count(*) total from icbc_erp_kj_icbc_result iekir,kj_icbc ki where iekir.status=81 and ki.id=iekir.icbc_id and ki.app=0
and ki.gems_fs_id=${gems_id}
and ki.id in(select icbc_id from kjs_icbc_kk where year(SYSDATE())-year(dt_add) < 6 and kk_loan_stateid=${city_id})
and year(ki.dt_add)=${timedate}
and ki.gems_fs_id in(select id from assess_fs af where up_id=${up_id} or id =${id}) and year(SYSDATE())-year(iekir.dt_sub) < 6 GROUP BY year(dt_sub),month(iekir.dt_sub) ORDER BY iekir.dt_sub DESC limit 9

select newcars.year1,newcars.month1,oldcars.oldcon,oldcars.oldmoney,newcars.newcon,newcars.newmoney from (select year(fk_date) year1,month(fk_date) month1, count(*) oldcon,(sum(fk_money)/10000) oldmoney from icbc_erp_kj_icbc_result where icbc_id in(select ac.icbc_id from assess_cars ac,kj_icbc ki where ac.cars_type=2 and ki.app=0 and ki.id=ac.icbc_id
and ki.gems_fs_id=${gems_id}
and ki.id in(select icbc_id from kjs_icbc_kk where year(SYSDATE())-year(dt_add) < 6 and kk_loan_stateid=${city_id})
and year(ki.dt_add)=${timedate}
and ki.gems_fs_id in(select id from assess_fs af where up_id=${up_id} or id =${id})) and `status`=62 and result_1_code=1 and year(SYSDATE())-year(fk_date) < 6 GROUP BY year(fk_date),month(fk_date) ORDER BY fk_date DESC limit 12) oldcars, (select year(fk_date) year1,month(fk_date) month1, count(*) newcon,(sum(fk_money)/10000) newmoney from icbc_erp_kj_icbc_result where icbc_id in(select ac.icbc_id from assess_cars ac,kj_icbc ki where ac.cars_type=1 and ki.id=ac.icbc_id and ki.app=0
and ki.gems_fs_id=${gems_id}
and ki.id in(select icbc_id from kjs_icbc_kk where year(SYSDATE())-year(dt_add) < 6 and kk_loan_stateid=${city_id})
and year(ki.dt_add)=${timedate}
and ki.gems_fs_id in(select id from assess_fs af where up_id=${up_id} or id =${id})) and `status`=62 and result_1_code=1 and year(SYSDATE())-year(fk_date) < 6 GROUP BY year(fk_date),month(fk_date) ORDER BY fk_date DESC limit 12) newcars where oldcars.year1=newcars.year1 and oldcars.month1=newcars.month1

select sum(case when to_days(pawn.d1) - to_days(pawn.d) < 15 then 1 end) paw1, sum(case when to_days(pawn.d1) - to_days(pawn.d) >= 15 and to_days(pawn.d1) - to_days(pawn.d) < 30 then 1 end) paw2, sum(case when to_days(pawn.d1) - to_days(pawn.d) >= 30 and to_days(pawn.d1) - to_days(pawn.d) < 45 then 1 end) paw3, sum(case when to_days(pawn.d1) - to_days(pawn.d) >= 45 and to_days(pawn.d1) - to_days(pawn.d) < 60 then 1 end) paw4, sum(case when to_days(pawn.d1) - to_days(pawn.d) >= 60 then 1 end) paw5 from (select iekir1.qryid,iekir1.status s,iekir1.dt_sub d,iekir2.status s1,iekir2.dt_sub d1 from (select ieki.qryid,ieki.status,ieki.dt_sub from icbc_erp_kj_icbc_result ieki,kj_icbc ki where ieki.status=72 and ki.app=0
and ki.gems_fs_id=${gems_id}
and ki.id in(select icbc_id from kjs_icbc_kk where year(SYSDATE())-year(dt_add) < 6 and kk_loan_stateid=${city_id})
and year(ki.dt_add)=${timedate}
and ki.gems_fs_id in(select id from assess_fs af where up_id=${up_id} or id =${id}) and ki.id=ieki.icbc_id) iekir1, (select ieki.qryid,ieki.status,ieki.dt_sub from icbc_erp_kj_icbc_result ieki,kj_icbc ki where ieki.status=81 and ki.app=0
and ki.gems_fs_id=${gems_id}
and ki.id in(select icbc_id from kjs_icbc_kk where year(SYSDATE())-year(dt_add) < 6 and kk_loan_stateid=${city_id})
and year(ki.dt_add)=${timedate}
and ki.gems_fs_id in(select id from assess_fs af where up_id=${up_id} or id =${id}) and ki.id=ieki.icbc_id) iekir2 where iekir1.qryid=iekir2.qryid ) pawn

select year(kic.dt_sub) year,month(kic.dt_sub) month,count(*) total from kjs_icbc_cardk kic,kj_icbc ki where kic.bc_status=3 and ki.id=kic.icbc_id and ki.app=0 and kic.gems_fs_id in(select id from assess_fs af where up_id=${up_id} or id =${id}) and year(SYSDATE())-year(kic.dt_sub) < 6
and kic.gems_fs_id=${gems_id}
and kic.icbc_id in(select icbc_id from kjs_icbc_kk where year(SYSDATE())-year(dt_add) < 6 and kk_loan_stateid=${city_id})
and year(kic.dt_add)=${timedate}
GROUP BY year(kic.dt_sub),month(kic.dt_sub) ORDER BY kic.dt_sub DESC limit 9

select ac.cars_type cartype,count(ac.cars_type) cartotal from assess_cars ac where ac.icbc_id in(select ki.id from kj_icbc ki,icbc_erp_kj_icbc_result iekir where month(iekir.fk_date)=MONTH(SYSDATE()) and YEAR(iekir.fk_date)=year(SYSDATE()) and ki.id=iekir.icbc_id and iekir.`status`=62 and ki.app=0 and iekir.result_1_code=1
and ki.gems_fs_id=${gems_id}
and ki.id in(select icbc_id from kjs_icbc_kk where year(SYSDATE())-year(dt_add) < 6 and kk_loan_stateid=${city_id})
and year(ki.dt_add)=${timedate}
and ki.gems_fs_id in(select id from assess_fs af where up_id=${up_id} or id =${id}) ) GROUP BY ac.cars_type ORDER BY ac.cars_type
